import React, { createContext, useContext, useState, useEffect } from 'react';

const ThemeContext = createContext();

export const useTheme = () => {
  const context = useContext(ThemeContext);
  if (!context) {
    throw new Error('useTheme must be used within a ThemeProvider');
  }
  return context;
};

export const ThemeProvider = ({ children }) => {
  const [isDarkMode, setIsDarkMode] = useState(() => {
    // Check localStorage first, then system preference
    const savedTheme = localStorage.getItem('gitpeek-theme');
    if (savedTheme) {
      return savedTheme === 'dark';
    }
    // Default to system preference
    return window.matchMedia('(prefers-color-scheme: dark)').matches;
  });

  useEffect(() => {
    // Save theme preference to localStorage
    localStorage.setItem('gitpeek-theme', isDarkMode ? 'dark' : 'light');

    // Apply theme to document root
    document.documentElement.setAttribute('data-theme', isDarkMode ? 'dark' : 'light');
  }, [isDarkMode]);

  const toggleTheme = () => {
    setIsDarkMode(prev => !prev);
  };

  const theme = {
    isDarkMode,
    toggleTheme,
    colors: isDarkMode ? darkTheme : lightTheme
  };

  return (
    <ThemeContext.Provider value={theme}>
      {children}
    </ThemeContext.Provider>
  );
};

// Light theme colors
const lightTheme = {
  background: '#ffffff',
  surface: '#f8fafc',
  surfaceSecondary: '#f1f5f9',
  border: '#e2e8f0',
  borderLight: '#f1f5f9',
  text: '#1e293b',
  textSecondary: '#64748b',
  textMuted: '#94a3b8',
  primary: '#3b82f6',
  primaryHover: '#2563eb',
  success: '#10b981',
  warning: '#f59e0b',
  error: '#ef4444',
  accent: '#8b5cf6',
  // GitHub colors for git graph
  gitMain: '#238636',
  gitFeature: '#0969da',
  gitHotfix: '#d1242f',
  gitBranch: '#8250df'
};

// Dark theme colors
const darkTheme = {
  background: '#0f172a',
  surface: '#1e293b',
  surfaceSecondary: '#334155',
  border: '#475569',
  borderLight: '#64748b',
  text: '#f8fafc',
  textSecondary: '#cbd5e1',
  textMuted: '#94a3b8',
  primary: '#60a5fa',
  primaryHover: '#3b82f6',
  success: '#34d399',
  warning: '#fbbf24',
  error: '#f87171',
  accent: '#a78bfa',
  // GitHub colors for git graph (slightly adjusted for dark mode)
  gitMain: '#3fb950',
  gitFeature: '#2f81f7',
  gitHotfix: '#f85149',
  gitBranch: '#a5a5f6'
};
