import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Navigate } from 'react-router-dom';

// Minimal code-themed canvas background component
const CodeBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    const updateCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);

    // Code elements that will float
    const codeElements = [
      'git commit', 'function()', 'const data', '{ }', 'return', 'import',
      'export', '++', '--', '=>', 'async', 'await', '.map()', '.filter()',
      'if()', 'else', 'for', 'let', 'var', 'class', 'new', 'this'
    ];

    // Floating code particles
    const particles = [];
    const maxParticles = 25; // Minimal amount

    // Create particles
    for (let i = 0; i < maxParticles; i++) {
      particles.push({
        x: Math.random() * canvas.width,
        y: Math.random() * canvas.height,
        text: codeElements[Math.floor(Math.random() * codeElements.length)],
        vx: (Math.random() - 0.5) * 0.5, // Very slow movement
        vy: (Math.random() - 0.5) * 0.5,
        opacity: Math.random() * 0.3 + 0.1, // Very subtle
        fontSize: Math.random() * 8 + 10
      });
    }

    // Animation function
    const animate = () => {
      // Clear canvas with very subtle background
      ctx.fillStyle = 'rgba(248, 250, 252, 0.95)';
      ctx.fillRect(0, 0, canvas.width, canvas.height);

      // Update and draw particles
      particles.forEach(particle => {
        // Move particle
        particle.x += particle.vx;
        particle.y += particle.vy;

        // Wrap around edges
        if (particle.x > canvas.width + 50) particle.x = -50;
        if (particle.x < -50) particle.x = canvas.width + 50;
        if (particle.y > canvas.height + 50) particle.y = -50;
        if (particle.y < -50) particle.y = canvas.height + 50;

        // Draw particle
        ctx.font = `${particle.fontSize}px 'Monaco', 'Menlo', monospace`;
        ctx.fillStyle = `rgba(71, 85, 105, ${particle.opacity})`;
        ctx.fillText(particle.text, particle.x, particle.y);
      });

      requestAnimationFrame(animate);
    };

    animate();

    // Cleanup
    return () => {
      window.removeEventListener('resize', updateCanvasSize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
};

function Home() {
  const { isAuthenticated, login, loading } = useAuth();
  const [loginLoading, setLoginLoading] = useState(false);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (isAuthenticated) {
    return <Navigate to="/dashboard" replace />;
  }

  const handleLogin = async () => {
    setLoginLoading(true);
    try {
      await login();
    } catch (error) {
      console.error('Login failed:', error);
      setLoginLoading(false);
    }
  };

  return (
    <div className="container">
      <div style={{ textAlign: 'center', padding: '60px 20px' }}>
        <h1 style={{ fontSize: '48px', marginBottom: '24px', color: '#1f2937' }}>
          GitPeek
        </h1>
        <p style={{ fontSize: '20px', marginBottom: '48px', color: '#6b7280', maxWidth: '600px', margin: '0 auto 48px' }}>
          Analyze your repositories with powerful insights into code ownership, 
          contributor activity, and dependency risks. Connect your GitHub account to get started.
        </p>

        <div className="card" style={{ maxWidth: '500px', margin: '0 auto', textAlign: 'left' }}>
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>What You'll Get:</h2>
          <ul style={{ lineHeight: '1.8', color: '#4b5563' }}>
            <li><strong>Code Ownership Analysis:</strong> See who primarily edits which files and folders</li>
            <li><strong>Knowledge Areas:</strong> Identify top contributors per module or directory</li>
            <li><strong>Activity Heatmaps:</strong> Visualize commits and PR reviews over time</li>
            <li><strong>Dependency Risk Maps:</strong> Understand impact if key contributors leave</li>
          </ul>
        </div>

        <div style={{ margin: '48px 0' }}>
          <div style={{ 
            backgroundColor: '#f3f4f6', 
            padding: '20px', 
            borderRadius: '8px', 
            marginBottom: '24px',
            maxWidth: '600px',
            margin: '0 auto 24px'
          }}>
            <h3 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>
              Required Permissions
            </h3>
            <p style={{ margin: 0, color: '#6b7280', fontSize: '14px' }}>
              This app will request access to:
            </p>
            <ul style={{ margin: '12px 0 0 0', color: '#6b7280', fontSize: '14px' }}>
              <li><strong>repo:</strong> Read-only access to your repositories</li>
              <li><strong>read:org:</strong> Read organization membership information</li>
            </ul>
            <p style={{ margin: '12px 0 0 0', color: '#6b7280', fontSize: '12px' }}>
              Your OAuth tokens are stored securely server-side and never exposed to the frontend.
            </p>
          </div>

          <button 
            onClick={handleLogin}
            disabled={loginLoading}
            className="button"
            style={{ fontSize: '18px', padding: '16px 32px' }}
          >
            {loginLoading ? 'Connecting...' : 'Connect with GitHub'}
          </button>
        </div>

        <div style={{ marginTop: '60px', color: '#9ca3af', fontSize: '14px' }}>
          <p>Secure • Private • Read-only access</p>
        </div>
      </div>
    </div>
  );
}

export default Home;
