import React, { useState, useEffect, useRef } from 'react';
import { useAuth } from '../contexts/AuthContext';
import { Navigate } from 'react-router-dom';

// Minimal GitHub-themed git graph background
const GitGraphBackground = () => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext('2d');

    // Set canvas size
    const updateCanvasSize = () => {
      canvas.width = window.innerWidth;
      canvas.height = window.innerHeight;
    };

    updateCanvasSize();
    window.addEventListener('resize', updateCanvasSize);

    // GitHub color scheme
    const colors = {
      light: 'rgba(22, 27, 34, 0.03)',     // Very subtle dark
      medium: 'rgba(22, 27, 34, 0.06)',    // GitHub dark
      accent: 'rgba(33, 136, 56, 0.08)',   // GitHub green
      branch: 'rgba(138, 43, 226, 0.05)'   // GitHub purple
    };

    // Create minimal git graph nodes
    const nodes = [];
    const connections = [];
    const maxNodes = 12;

    // Create nodes in a loose grid pattern
    for (let i = 0; i < maxNodes; i++) {
      const node = {
        x: (canvas.width / 6) + (i % 4) * (canvas.width / 5),
        y: (canvas.height / 4) + Math.floor(i / 4) * (canvas.height / 3),
        targetX: 0,
        targetY: 0,
        radius: Math.random() * 2 + 2,
        color: i % 3 === 0 ? colors.accent : colors.medium,
        pulse: Math.random() * Math.PI * 2,
        pulseSpeed: 0.01 + Math.random() * 0.02
      };

      // Add subtle random offset
      node.x += (Math.random() - 0.5) * 100;
      node.y += (Math.random() - 0.5) * 80;
      node.targetX = node.x;
      node.targetY = node.y;

      nodes.push(node);
    }

    // Create connections between nearby nodes
    for (let i = 0; i < nodes.length; i++) {
      for (let j = i + 1; j < nodes.length; j++) {
        const dx = nodes[i].x - nodes[j].x;
        const dy = nodes[i].y - nodes[j].y;
        const distance = Math.sqrt(dx * dx + dy * dy);

        if (distance < 200 && Math.random() > 0.6) {
          connections.push({
            from: i,
            to: j,
            opacity: 0.1 + Math.random() * 0.1
          });
        }
      }
    }

    let time = 0;

    // Animation function
    const animate = () => {
      time += 0.01;

      // Clear canvas
      ctx.clearRect(0, 0, canvas.width, canvas.height);

      // Draw connections (git branches)
      ctx.strokeWidth = 1;
      connections.forEach(conn => {
        const fromNode = nodes[conn.from];
        const toNode = nodes[conn.to];

        ctx.beginPath();
        ctx.moveTo(fromNode.x, fromNode.y);
        ctx.lineTo(toNode.x, toNode.y);
        ctx.strokeStyle = colors.branch;
        ctx.globalAlpha = conn.opacity;
        ctx.stroke();
      });

      // Draw nodes (git commits)
      nodes.forEach(node => {
        // Subtle floating animation
        node.pulse += node.pulseSpeed;
        const pulse = Math.sin(node.pulse) * 0.5;

        // Very gentle movement
        const offsetX = Math.sin(time + node.pulse) * 2;
        const offsetY = Math.cos(time * 0.7 + node.pulse) * 1.5;

        ctx.beginPath();
        ctx.arc(
          node.x + offsetX,
          node.y + offsetY,
          node.radius + pulse,
          0,
          Math.PI * 2
        );
        ctx.fillStyle = node.color;
        ctx.globalAlpha = 0.6 + pulse * 0.2;
        ctx.fill();
      });

      ctx.globalAlpha = 1;
      requestAnimationFrame(animate);
    };

    animate();

    // Cleanup
    return () => {
      window.removeEventListener('resize', updateCanvasSize);
    };
  }, []);

  return (
    <canvas
      ref={canvasRef}
      style={{
        position: 'fixed',
        top: 0,
        left: 0,
        width: '100%',
        height: '100%',
        zIndex: -1,
        pointerEvents: 'none'
      }}
    />
  );
};

function Home() {
  const { isAuthenticated, login, loading } = useAuth();
  const [loginLoading, setLoginLoading] = useState(false);

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  if (isAuthenticated) {
    return <Navigate to="/dashboard" replace />;
  }

  const handleLogin = async () => {
    setLoginLoading(true);
    try {
      await login();
    } catch (error) {
      console.error('Login failed:', error);
      setLoginLoading(false);
    }
  };

  return (
    <>
      <CodeBackground />
      <div className="container" style={{ position: 'relative', zIndex: 1 }}>
        <div style={{ textAlign: 'center', padding: '60px 20px' }}>
        <h1 style={{ fontSize: '48px', marginBottom: '24px', color: '#1f2937' }}>
          GitPeek
        </h1>
        <p style={{ fontSize: '20px', marginBottom: '48px', color: '#6b7280', maxWidth: '600px', margin: '0 auto 48px' }}>
          Analyze your repositories with powerful insights into code ownership, 
          contributor activity, and dependency risks. Connect your GitHub account to get started.
        </p>

        <div className="card" style={{ maxWidth: '500px', margin: '0 auto', textAlign: 'left' }}>
          <h2 style={{ marginBottom: '20px', color: '#1f2937' }}>What You'll Get:</h2>
          <ul style={{ lineHeight: '1.8', color: '#4b5563' }}>
            <li><strong>Code Ownership Analysis:</strong> See who primarily edits which files and folders</li>
            <li><strong>Knowledge Areas:</strong> Identify top contributors per module or directory</li>
            <li><strong>Activity Heatmaps:</strong> Visualize commits and PR reviews over time</li>
            <li><strong>Dependency Risk Maps:</strong> Understand impact if key contributors leave</li>
          </ul>
        </div>

        <div style={{ margin: '48px 0' }}>
          <div style={{ 
            backgroundColor: '#f3f4f6', 
            padding: '20px', 
            borderRadius: '8px', 
            marginBottom: '24px',
            maxWidth: '600px',
            margin: '0 auto 24px'
          }}>
            <h3 style={{ margin: '0 0 12px 0', color: '#1f2937' }}>
              Required Permissions
            </h3>
            <p style={{ margin: 0, color: '#6b7280', fontSize: '14px' }}>
              This app will request access to:
            </p>
            <ul style={{ margin: '12px 0 0 0', color: '#6b7280', fontSize: '14px' }}>
              <li><strong>repo:</strong> Read-only access to your repositories</li>
              <li><strong>read:org:</strong> Read organization membership information</li>
            </ul>
            <p style={{ margin: '12px 0 0 0', color: '#6b7280', fontSize: '12px' }}>
              Your OAuth tokens are stored securely server-side and never exposed to the frontend.
            </p>
          </div>

          <button 
            onClick={handleLogin}
            disabled={loginLoading}
            className="button"
            style={{ fontSize: '18px', padding: '16px 32px' }}
          >
            {loginLoading ? 'Connecting...' : 'Connect with GitHub'}
          </button>
        </div>

        <div style={{ marginTop: '60px', color: '#9ca3af', fontSize: '14px' }}>
          <p>Secure • Private • Read-only access</p>
        </div>
        </div>
      </div>
    </>
  );
}

export default Home;
